package com.example.suffle.ui.Home.alert

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.widget.Toast
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.suffle.R
import com.example.suffle.data.NormalPersonData
import kotlinx.android.synthetic.main.activity_alert.*

class AlertActivity : AppCompatActivity() {

    val alertDatas = mutableListOf<NormalPersonData>()
    lateinit var alertAdpater: AlertAdpater

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_alert)

        act_alert_btn_back.setOnClickListener {
            finish()
        }

        //adapter initialization
        alertAdpater = AlertAdpater(this,
            object : AlertViewHolder.onDeleteClickListener {
                override fun onDeleteClick(position: Int) {
                    Toast.makeText(this@AlertActivity, position.toString(), Toast.LENGTH_SHORT)
                        .show()
                }
            },
            object : AlertViewHolder.onAllowClickListener {
                override fun onAllowClick(position: Int) {
                    Toast.makeText(this@AlertActivity, position.toString(), Toast.LENGTH_SHORT)
                        .show()
                }

            }
        )

        act_alert_rv.adapter = alertAdpater
        act_alert_rv.layoutManager = LinearLayoutManager(this, LinearLayoutManager.VERTICAL, false)

        loadDatas()
    }

    private fun loadDatas(){
        alertDatas.apply {
            add(
                NormalPersonData(
                    img_person = "data:image/png;base64,",
                    txt_personName = "장윤주"
                )
            )
            add(
                NormalPersonData(
                    img_person = "data:image/jpeg;base64,",
                    txt_personName = "장윤주"
                )
            )
            add(
                NormalPersonData(
                    img_person = "data:image/png;base64,",
                    txt_personName = "장윤주"
                )
            )
            add(
                NormalPersonData(
                    img_person = "data:image/jpeg;base64,",
                    txt_personName = "장윤주"
                )
            )
            add(
                NormalPersonData(
                    img_person = "data:image/png;base64,",
                    txt_personName = "장윤주"
                )
            )
            add(
                NormalPersonData(
                    img_person = "data:image/jpeg;base64,",
                    txt_personName = "장윤주"
                )
            )
        }

        alertAdpater.datas = alertDatas
        alertAdpater.notifyDataSetChanged()
    }
}